mapper.update:
UPDATE product
prod_name = #{prodName} , prod_detail = #{prodDetail} , manufacture_day= TO_DATE(#{manuDate},'YYYY-MM-DD') , price = #{price} , image_file = #{fileName}
WHERE prod_no = #{prodNo}

mapper.select:
SELECT * FROM ( SELECT inner_table.* , ROWNUM AS row_seq FROM ( SELECT p.* , NVL(t.tran_status_code, '000') pro_tran_status_code FROM product p , transaction t
p.prod_no = t.prod_no(+)
AND p.prod_no LIKE LIKE '%' || #{searchKeyword} || '%'
AND p.prod_name LIKE '%' || #{searchKeyword} || '%'
AND p.price = #{searchKeyword}
ORDER BY p.prod_no ) inner_table WHERE ROWNUM <= #{endRowNum} ) WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

SELECT COUNT(*) FROM( SELECT p.* , NVL(t.tran_status_code, '000') pro_tran_status_code FROM product p , transaction t
p.prod_no = t.prod_no(+)
AND p.prod_no LIKE '%' || #{searchKeyword} || '%'
AND p.prod_name LIKE '%' || #{searchKeyword} || '%'
AND p.price = #{searchKeyword}
) countTable
