payment_option = #{paymentOption }, RECEIVER_NAME = #{receiverName }, receiver_phone = #{receiverPhone}, DEMAILADDR = #{dlvyAddr}, dlvy_request = #{dlvyRequest }, dlvy_date = TO_DATE(#{dlvyDate}, 'YYYY-MM-DD')
WHERE tran_no = #{tranNo}

